function initMap(o){map=new L.Map("map",{attributionControl:!1,zoomControl:!1,scrollWheelZoom:!1,dragging:!1});var a=new L.TileLayer("https://api.mapbox.com/styles/v1/mapbox/dark-v9/tiles/256/{z}/{x}/{y}?access_token={accessToken}",{minZoom:1,maxZoom:16,attribution:'&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a>',accessToken:"pk.eyJ1IjoicmFwaXJlbnQiLCJhIjoiY2oybXBsc2ZvMDE2YjMycGg4NzNkYXI0OSJ9.xC8TBirQ2os2eQ65hwTCMA"}),n=new L.LatLng(o.lamp_location[1],o.lamp_location[0]);map.setView(n,16),a.addTo(map);var t=L.icon({iconUrl:"dist/img/legend.svg",iconSize:[45,80],popupAnchor:[0,-40],iconAnchor:[22,60]});console.log(o.lamp_location[1]),L.marker(n,{icon:t,opacity:.9}).addTo(map).bindPopup("<strong>標號：</strong>"+o.id+"<br><strong>座標：</strong>"+o.lamp_location+"<br><strong>佈署日期：</strong>"+o.lamp_deployed_data+"<br><strong>地區編號：</strong>"+o.place_id,{keepInView:!0}).openPopup()}